/**
 * n8n Code node (Run Once for All Items)
 * Duplicates the first input item gen_params.repetition times,
 * incrementing gen_params.seed each time.
 *
 * Output: array of items [{ json: ... }, { json: ... }, ...]
 * suitable for Split in Batches.
 */

const inputItems = $input.all();
if (!inputItems.length) return [];

const inputJson = inputItems[0].json;

// Basic validation
if (!inputJson.gen_params) {
  throw new Error('Input JSON must contain "gen_params".');
}
if (typeof inputJson.gen_params.repetition !== 'number') {
  throw new Error('"gen_params.repetition" must be a number.');
}
if (typeof inputJson.gen_params.seed !== 'number') {
  throw new Error('"gen_params.seed" must be a number.');
}

const repetition = Math.floor(inputJson.gen_params.repetition);
if (repetition < 0) {
  throw new Error('"gen_params.repetition" must be >= 0.');
}

let seed = inputJson.gen_params.seed;

const out = [];
for (let i = 0; i < repetition; i++) {
  // Deep clone so each item is independent
  const cloned = JSON.parse(JSON.stringify(inputJson));

  // Set incremented seed
  cloned.gen_params.seed = seed;

  out.push({ json: cloned });

  seed++;
}

return out;

const items = $input.all();

// Keep only items that have json and at least one key
const filtered = items.filter(i =>
  i.json && typeof i.json === 'object' && Object.keys(i.json).length > 0
);

// Return in valid n8n format
return filtered.map(i => ({ json: i.json }));
